import { Component, OnInit } from '@angular/core';
import { PersonaService } from '../services/persona.service';
import { Persona } from '../model/persona.model';

@Component({
  selector: 'app-newacercade',
  templateUrl: './newacercade.component.html',
  styleUrls: ['./newacercade.component.css']
})
export class NewacercadeComponent implements OnInit {
  persona: Persona = new Persona('', '', '', '');
  imageService: any = {
    url: ''
  };

  constructor(private personaService: PersonaService) { }

  ngOnInit(): void {
  }

  onCreate(): void {
    this.personaService.save(this.persona).subscribe(
      response => {
        console.log(response);
        alert('Persona creada correctamente');
      },
      error => {
        console.log(error);
        alert('Ocurri칩 un error al crear la persona');
      }
    );
  }

  uploadImage(event: any): void {
    if (event.target.files && event.target.files[0]) {
      const reader = new FileReader();
      reader.onload = (e: any) => {
        this.imageService.url = e.target.result;
      };
      reader.readAsDataURL(event.target.files[0]);
      this.persona.img = event.target.files[0];
    }
  }
}





