import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Skill } from 'src/app/model/skill';
import { SkillService } from 'src/app/service/skill.service';

@Component({
  selector: 'app-new-skill',
  templateUrl: './new-skill.component.html',
  styleUrls: ['./new-skill.component.css']
})
export class NewSkillComponent implements OnInit{
  nombre: string;
  porcentaje: number;

  constructor(private skillS:SkillService, private router:Router){}

  ngOnInit(): void {
  }

  onCreate():void{
    const skill = new Skill(this.nombre, this.porcentaje);
    this.skillS.save(skill).subscribe(
      data=>{
        alert("Skill creada correctamente");
        this.router.navigate(['']);
      },err=>{
        alert("Fallo al añadir la skill");
        this.router.navigate(['']);
      }
    )
  }

}
<nav class="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
  <div class="container-fluid g-0">

    <a class="navbar-brand" href="#">
      <img src="assets/logoYP.png" alt="logo" width="130" height="">Denise Lucía Bustos
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

  </div>
</nav>

<div class="container-fluid">
  <div class="row my-5">
    <h2 class="display-5 my-4 text-center">Hard&Soft Skills</h2>

    <div class="col-10 col-md-6 mx-auto">

      <form (ngSubmit)="onCreate()" novalidate #f="ngForm" action="">
        <div class="form-group my-4">
          <label for="nombre" class="h4 fw-normal">Nueva Skill</label>
          <input type="text" class="form-control" id="nombre" name="nombre" [(ngModel)]="nombre" required>
        </div>
        <div class="form-group my-4">
          <label for="porcentaje" class="h4 fw-normal">Porcentaje</label>
          <input type="text" class="form-control" id="porcentaje" name="porcentaje" [(ngModel)]="porcentaje" required>
        </div>

        <button class="btn btn-primary">Agregar Skill</button>
      </form>
    </div>
  </div>
</div>
