import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/service/educacion.service';

@Component({
  selector: 'app-neweducacion',
  templateUrl: './neweducacion.component.html',
  styleUrls: ['./neweducacion.component.css']
})
export class NeweducacionComponent implements OnInit{
  nombreE:string;
  descripcionE:string;

  constructor(private educacionS: EducacionService, private router: Router){}

  ngOnInit(): void {
  }

  onCreate():void{
    const educacion = new Educacion(this.nombreE, this.descripcionE);
    this.educacionS.save(educacion).subscribe(
      data=>{
        alert("Educación añadida correctamente");
        this.router.navigate(['']);
      }, err=>{
        this.router.navigate(['']);
      }
    )
  }

}
<nav class="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
  <div class="container-fluid g-0">

    <a class="navbar-brand" href="#">
      <img src="assets/logoYP.png" alt="logo" width="130" height="">Denise Lucía Bustos
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

  </div>
</nav>

<div class="container-fluid" >
  <div class="row my-5">
    <h2 class="display-5 my-4 text-center">Editar Educación</h2>

    <div class="col-10 col-md-8 mx-auto">

      <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
        <div class="form-group my-4">
          <label for="nombreE" class="h4 fw-normal">Nombre de la Institución</label>
          <input type="text" class="form-control" id="nombreE" name="nombreE" [(ngModel)]="nombreE" required>
        </div>
        <div class="form-group my-4">
          <label for="descripcionE" class="h4 fw-normal">Descripcion de sus estudios</label>
          <input type="text" class="form-control" id="descripcionE" name="descripcionE" [(ngModel)]="descripcionE" required>
        </div>
        <button class="btn btn-primary">Agregar Educación</button>
      </form>

    </div>
  </div>
</div>
