import { Component, OnInit } from '@angular/core';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/service/educacion.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-educacion',
  templateUrl: './educacion.component.html',
  styleUrls: ['./educacion.component.css']
})
export class EducacionComponent implements OnInit{
  educacion: Educacion[] = [];

  constructor(private educacionS: EducacionService, private tokenService: TokenService){}
  isLogged = false;

  ngOnInit(): void {
    this.cargarEducacion();
    if(this.tokenService.getToken()){
      this.isLogged = true;
    }else{
      this.isLogged = false;
    }
  }

  cargarEducacion(): void{
    this.educacionS.lista().subscribe(
      data=>{
        this.educacion = data;
      }
    )
  }

  borrar(id?:number){
    if(id != undefined){
      this.educacionS.delete(id).subscribe(
        data=>{
          this.cargarEducacion();
        },err=>{
          alert("No se pudo eliminar");
        }
      )
    }
  }

}
<div id="educ" class="container-fluid g-0">
  <h2 class="text-center m-5 p-3 text-bg-primary">Educación</h2>

  <div class="row justify-content-center">
    <div class="col-12 col-md-6 col-lg-4 text-center">
      <button type="button" class="btn btn-outline-primary btn-lg mx-2 p-3">
        <i class="bi bi-file-earmark-plus-fill"></i> Nuevo
      </button>
    </div>
  </div>

  <div class="row justify-content-center my-4">
    <div class="col-12 col-md-6 col-lg-4 text-center">
      <button type="button" class="btn btn-primary btn-lg m-2 p-3">
        <i class="bi bi-file-earmark-plus-fill"></i>
        Nueva Educación</button>
    </div>
  </div>


  <div class="row g-0 my-5 ">
    <div class="col-10 col-sm-8 col-md-4 text-center my-5">
      <img class="w-25" src="assets/experiencia/logoempresa.jpg" alt="">
    </div>
    <div  class="col-6 col-md-5 mx-4">
      <h2 class="display-5 mb-4">Educación Primaria</h2>
      <p class="h4 fw-light">(years)</p>
      <p class="h4 fw-normal">Descripción Educación</p>
    </div>
    <div class="col-3 col-md-2 ">
      <button type="button" class="btn btn-outline-primary mx-2">
        <i class="bi bi-pencil-fill"></i> Editar
      </button>
      <button type="button" class="btn btn-outline-primary mx-2">
        <i class="bi bi-trash3"></i>Borrar
      </button>
    </div>
  </div>

  <div class="row g-0 my-4 align-items-center">
    <div class="col-2 col-md-4 text-center my-5">
      <img class="w-25" src="assets/experiencia/logoempresa.jpg" alt="">
    </div>
    <div class="col-8 col-md-6">
      <h2 class="display-5 mb-4">Educación Secundaria</h2>
      <p class="h4 fw-light">Periodo Sep.17-Dic.17 2018</p>
      <p class="h4 fw-normal">Lorem ipsum dolor sit amet consectetur adipisicing elit. Esse, aperiam!</p>
    </div>
  </div>

  <div class="row g-0 my-4 align-items-center">
    <div class="col-2 col-md-4 text-center my-5">
      <img class="w-25" src="assets/experiencia/logoempresa.jpg" alt="">
    </div>
    <div class="col-8 col-md-6">
      <h2 class="display-5 mb-4">Educación Terciarios</h2>
      <p class="h4 fw-light">Periodo Sep17-Dic17 2018</p>
      <p class="h4 fw-normal">Lorem ipsum dolor sit amet consectetur adipisicing elit. Cumque, eaque?</p>
    </div>
  </div>


</div>
