import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Experiencia } from 'src/app/model/experiencia';
import { SExperienciaService } from 'src/app/service/s-experiencia.service';

@Component({
  selector: 'app-new-experiencia',
  templateUrl: './new-experiencia.component.html',
  styleUrls: ['./new-experiencia.component.css']
})
export class NewExperienciaComponent implements OnInit{
  nombreE: string = '';
  descripcionE: string= '';

  constructor(private sExperiencia: SExperienciaService, private router:Router){}

  ngOnInit(): void {
  }

  onCreate():void{
    const expe = new Experiencia(this.nombreE, this.descripcionE);
    this.sExperiencia.save(expe).subscribe(
      data=>{
        alert("Experiencia añadida");
        this.router.navigate(['']);
      }, err=>{
        alert("Falló");
        this.router.navigate(['']);
      }
    )
  }

}
<nav class="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
  <div class="container-fluid g-0">

    <a class="navbar-brand" href="#">
      <img src="assets/logoYP.png" alt="logo" width="130" height="">Denise Lucía Bustos
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

  </div>
</nav>

<div class="container-fluid">
  <div class="row my-5">
    <h2 class="display-5 my-4 text-center">Nueva Experiencia</h2>
    <div class="col-10 col-md-6 mx-auto">
      <form (ngSubmit)="onCreate()" novalidate #f="ngForm" action="">
        <div class="form-group my-4">
          <label for="nombreE" class="h4 fw-normal">Nombre de la empresa</label>
          <input type="text" class="form-control" id="nombreE" name="nombreE" [(ngModel)]="nombreE" required>
        </div>
        <div class="form-group my-4">
          <label for="descripcionE" class="h4 fw-normal">Descripcion de la tarea</label>
          <input type="text" class="form-control" id="descripcionE" name="descripcionE" [(ngModel)]="descripcionE" required>
        </div>
        <button class="btn btn-primary">Agregar experiencia</button>
      </form>
    </div>
  </div>
</div>
