import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Proyecto } from 'src/app/model/proyecto';
import { ProyectoService } from 'src/app/service/proyecto.service';

@Component({
  selector: 'app-editproyecto',
  templateUrl: './editproyecto.component.html',
  styleUrls: ['./editproyecto.component.css']
})
export class EditproyectoComponent implements OnInit{
  proyecto: Proyecto = null;

  constructor(
    private proyectoS:ProyectoService,
    private activatedRouter: ActivatedRoute,
    private router: Router
  ){}

  ngOnInit(): void {
    const id = this.activatedRouter.snapshot.params['id'];
    this.proyectoS.detail(id).subscribe(
      data=>{
        this.proyecto=data;
      },err=>{
        alert("Error al modificar");
        this.router.navigate(['']);
      }
    )
  }

  onUpdate():void{
    const id = this.activatedRouter.snapshot.params['id'];
    this.proyectoS.update(id, this.proyecto).subscribe(
      data=>{
        this.router.navigate(['']);
      },err =>{
        alert("Error al modificar el proyecto");
        this.router.navigate(['']);
      }
    )
  }

}
<nav class="navbar navbar-expand-lg bg-primary" data-bs-theme="dark">
  <div class="container-fluid g-0">

    <a class="navbar-brand" href="#">
      <img src="assets/logoYP.png" alt="logo" width="130" height="">Denise Lucía Bustos
    </a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

  </div>
</nav>

<div class="container-fluid">
  <div class="row my-5">
    <h2 class="display-5 my-4 text-center">Editar Proyecto</h2>
    <div class="col-10 col-md-6 mx-auto">
      <form (ngSubmit)="onUpdate()" novalidate #f="ngForm" action="">
        <div class="form-group my-4">
          <label for="nombreP" class="h4 fw-normal">Nombre del proyecto</label>
          <input type="text" class="form-control" id="nombreP" name="nombreP" [(ngModel)]="nombreP" required>
        </div>
        <div class="form-group my-4">
          <label for="descripcionP" class="h4 fw-normal">Descripcion</label>
          <input type="text" class="form-control" id="descripcionP" name="descripcionP" [(ngModel)]="descripcionP" required>
        </div>
        <div class="form-group my-4">
          <label for="linkP" class="h4 fw-normal">Link del proyecto</label>
          <input type="text" class="form-control" id="linkP" name="linkP" [(ngModel)]="linkP" required>
        </div>
        <button class="btn btn-primary">Agregar proyecto</button>
      </form>
    </div>
  </div>
</div>
